mapper.select:
SELECT p.id, p.farmer_id, fi.farm_name, p.category_id, pc.name AS category_name, p.name, p.spec, p.unit, p.price, p.min_purchase, p.stock, p.production_date, p.shelf_life, p.production_method, p.origin_area_id, oa.area_name AS origin_area_name, p.description, p.status, p.create_time, pi.image_url, pi.image_type, pi.sort AS image_sort FROM farmer_product p LEFT JOIN farmer_info fi ON p.farmer_id = fi.id LEFT JOIN product_category pc ON p.category_id = pc.id LEFT JOIN origin_area oa ON p.origin_area_id = oa.area_id LEFT JOIN product_image pi ON p.id = pi.product_id
AND p.category_id = #{categoryId}
AND p.origin_area_id = #{originAreaId}
AND p.status = #{status}
ORDER BY p.create_time DESC, pi.sort ASC
